BeginPackage["colorBlender`"]
(* Exported symbols added here with SymbolName::usage *) 


colorBlender::usage = "A graphical user interface to create a custom color gradient. \
It takes either an integer (value >=2) \
or color gradients as input. \
If no argument is given, \
then a gradient made up of four random colors and positions is displayed.";
cbparse::nolist = "The argument `1` is not a pairwise list of numbers and colors.";
cbparse::range = "Warning: the argument `1` contains color positions outside of the range of 0 to 1.";
cbparse::numeric = "The argument `1` contains non-numeric color positions.";
cbparse::invalid = "The argument `1` is neither an integer, a pairwise list of numbers and colors, nor a list of pairwise lists of numbers and colors.";


Begin["`Private`"]
(* Implementation of the package *)

(*Slider to mimic Mathematica's standard DownArrow slider*)
mySlider[] = Graphics[{
	EdgeForm[{Thin, Black}],
	Polygon[{{0, 0}, {1, 0}, {1, -1.2}, {0.5, -1.5}, {0, -1.2}}, 
		VertexColors -> {GrayLevel[1], GrayLevel[1], GrayLevel[0.8], GrayLevel[0.8], GrayLevel[0.8]}]}, 
	ImageSize -> 15, AspectRatio -> 1.5]
  
(*Make backround for sliders (locators) to move on*)
sliderBars[element_List] := With[{n = Length[element]},
  {(*gray bars for sliders*)
   {Gray, Table[Line[{{-0.02, i}, {1.015, i}}], {i, n}]}, 
   (*thick vertical lines of the appropriate color*)
   Transpose[{ Map[Directive[#[[2]],Opacity[#[[3]]]]&, element], ConstantArray[Thick, n], Table[Line[{{element[[i, 1]], i}, {element[[i, 1]], 0}}], {i, n}] }], 
   (*sliders at the appropriate locations*)
   Table[ Inset[Graphics[mySlider[], ImageSize -> 14], {element[[i, 1]], i}], {i, n}]
  }
]

(*Graphical representation of blend of colors; size matches with slider bars*)
colorBlend[elements_List] := Graphics[
  Raster[{Range[1000]/1000}, 
    ColorFunction -> (Blend[Map[{#[[1]],Directive[#[[2]],Opacity[#[[3]]]]}&,elements], #] &)
  ], 
  AspectRatio -> 0.3, PlotRangePadding -> None, ImageSize -> 385]

(*Upper panel of sliders and ColorSliders*)  
colorPanel[Dynamic[elements_]] := DynamicModule[{ln, n = Length[elements]},
  Row[{
  	(*EventHandler allows "locators" to move like independent sliders*)
  	Column[ Table[
      With[{i = i}, 
        InputField[Dynamic[elements[[i,1]]],Appearance->"Frameless", FieldSize->Tiny]
      ], {i, n, 1, -1}], Spacings -> 2 ],
      
    EventHandler[
     Dynamic[Graphics[sliderBars[elements],
       PlotRangePadding -> None,
       PlotRange -> {{-0.021, 1.02}, {0.5, n + 0.5}},
       AspectRatio -> 0.016514285714285662` + 0.10488571428571436` n,
       ImageSize -> 400]],
     {"MouseDown" :> (
        ln = First[ Nearest[Range[n], Last@MousePosition["Graphics"]] ];
        elements[[ln, 1]] = Clip[First[ MousePosition["Graphics"] ], {0, 1}]
        ),
      "MouseDragged" :> (elements[[ln, 1]] =  Clip[First[ MousePosition["Graphics"] ], {0, 1}])
     }
    ],
    (*right column of color sliders, aligned to sliderBars by AspectRatio equation*)
    Column[ Table[
      With[{i = i}, 
        Row[{
          ColorSlider[Dynamic[elements[[i, 2]]]], 
          Spacer[10], 
          VerticalSlider[Dynamic[elements[[i, 3]]],{0,1},ImageSize->{Automatic,40}], 
          Spacer[10], 
          InputField[Dynamic[elements[[i, 3]]],Appearance->"Frameless",FieldSize->3],
          Spacer[10],
          i
        }]
      ], {i, n, 1, -1}], Spacings -> 0 ]
  }]
]
  
parseSingleInput[x_] := Module[{loc, col},
	(*check if pairwise list*)
  	If[! MatchQ[Dimensions[x], {_, 2}], Message[cbparse::nolist, x]; Return[False]];
  	{loc, col} = Transpose[x];
  	
  	(*check if first entries of pairwise list are numbers; issue warning if not from 0<x<1*)
  	If[! AllTrue[loc, NumericQ], Message[cbparse::numeric, x]; Return[False]];
  	If[! AllTrue[loc, 0 <= # <= 1 &], Message[cbparse::range, x]];
  	
  	(*check if last entries of pairwise list are colors*)
  	If[! AllTrue[col, ColorQ], Message[cbparse::nolist, x]; Return[False]];
  	
  	True
]
  
colorBlender[input_:{}] := 
  DynamicModule[{n = 1, el, custom, tf},
   Which[
   Head[input]===Integer, If[input>=2, custom={}; el = Transpose[{RandomReal[{0, 1}, input], RandomColor[input], ConstantArray[1, input]}], el=Null],
   input===Null || input==={}, custom={}; el = Transpose[{RandomReal[{0, 1}, 4], RandomColor[4], ConstantArray[1, 4]}],
   Head[input]===List, 
   	If[MatchQ[Dimensions[input],{_,2}], 
   		tf = parseSingleInput[input];
   			If[tf, custom = {input}; el = input, el = False], 
   		tf = Map[parseSingleInput,input];
   			If[AllTrue[tf,Identity], custom = input; el = First[input], el = False]
   	],
   Head[input]===Function && Head[input[[1]]]===Blend,
     el = input[[1,1]] /. Directive[x_, Opacity[y_]] :> Sequence[x,y],
   True, Message[cbparse::invalid,input]; el = False;
   ];
   
   If[ el===False, Defer[test[input]],  
   Deploy@Panel@Column[{
     Dynamic[colorPanel[Dynamic[el]]],
     Row[{
       Spacer[78],
       Dynamic[colorBlend[el]],
       Spacer[8],
       Panel[Column[{
          Dynamic[SetterBar[Dynamic[n], Range[Length[el]], Appearance -> "Horizontal" -> {Automatic, 12}]],
          Grid[{
            {Button["Remove color", If[Length[el] > 2, el = Delete[el, n]]],
             Button["Add color", el = Insert[el, {RandomReal[], RandomColor[], RandomReal[]}, n]]},
            {ActionMenu["Presets", Evaluate[presets[el, custom]]],
             (*Button["Output settings", 
             	SelectionMove[EvaluationNotebook[], After, Cell];
             	NotebookWrite[EvaluationNotebook[], Cell[ToString[Map[{#[[1]],Directive[#[[2]],Opacity[#[[3]]]]}&,el]], "Input"]]
             ]*)
             Dynamic[
               With[{aa=Map[{#[[1]],Directive[#[[2]],Opacity[#[[3]]]]}&, el]},
                 Button["Copy to clipboard", CopyToClipboard[Blend[aa,#]&]]
               ],
               TrackedSymbols:>{el}
             ]
             (*Button["Copy to clipboard", CopyToClipboard[Function[{x},Blend[Map[{#[[1]],Directive[#[[2]],Opacity[#[[3]]]]}&, el],x]]]]*)
            }
          }]
        }] ]
     }]
  }, Spacings -> 0, Alignment -> {Left, Top}]
  ], SaveDefinitions->True
]
  

 
 
